mapper.select:
select t.id,u1.name as owner,t.name,c.name as customer_id,dv1.value as stage, dv2.value as type,dv3.value as source,co.fullname as contacts_id from tbl_tran t join tbl_user u1 on u1.id=t.owner join tbl_customer c on c.id=t.customer_id join tbl_contacts co on co.id=t.contacts_id join tbl_dic_value dv1 on dv1.id=t.stage join tbl_dic_value dv2 on dv2.id=t.type join tbl_dic_value dv3 on dv3.id=t.source
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and c.name like '%' #{customerId} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}
and co.fullname like '%' #{contactsId} '%'
order by t.create_time desc limit #{beginNo},#{pageSize}

select count(*) from tbl_tran t join tbl_user u1 on u1.id=t.owner join tbl_customer c on c.id=t.customer_id join tbl_contacts co on co.id=t.contacts_id
and u1.name like '%' #{owner} '%'
and t.name like '%' #{name} '%'
and c.name like '%' #{customerId} '%'
and t.stage=#{stage}
and t.type=#{type}
and t.source=#{source}
and co.fullname like '%' #{contactsId} '%'

mapper.update:
update tbl_tran
set owner=#{owner},money=#{money},name=#{name},expected_date=#{expectedDate},customer_id=#{customerId},
    stage=#{stage},type=#{type},source=#{source},activity_id=#{activityId},contacts_id=#{contactsId},
    description=#{description},contact_summary=#{contactSummary},next_contact_time=#{nextContactTime},
    edit_by=#{editBy},edit_time=#{editTime}
where id=#{id}

mapper.delete:
delete from tbl_tran
where id in

  #{id}
