mapper.select:
select fullname
from tbl_contacts
where id=#{id}

select co.id,u1.name as owner,dv1.value as source,cu.name as customer_id,co.fullname,co.appellation, co.email,co.mphone,co.job,u2.name as create_by,co.create_time,co.description, co.contact_summary,co.next_contact_time,co.address from tbl_contacts co join tbl_user u1 on u1.id=co.owner left join tbl_dic_value dv1 on dv1.id=co.source join tbl_customer cu on cu.id=co.customer_id join tbl_user u2 on u2.id=co.create_by
and u1.name like '%' #{owner} '%'
and co.fullname like '%' #{fullname} '%'
and cu.name like concat('%',#{customerId},'%')
and co.source = #{source}
order by co.create_time desc limit #{beginNo},#{pageSize}

mapper.delete:
delete from tbl_contacts
where id in

  #{id}

mapper.select:
select c.id,c.fullname,c.email,c.mphone
from tbl_contacts c
where c.customer_id=#{customerId}
